const css = require('css');
const EOF = Symbol("EOF");
const layout = require('./layout.js');
let currentToken = null;
let currentAttribute = null;
let currentTextNode = null;

let stack = [{type: "document", children: []}]

let rules = [];
function addCSSRules(text) {
  let ast = css.parse(text);
  rules.push(...ast.stylesheet.rules);
}

function match(element, allSelector) {
  if (!allSelector || !element.attributes) {
    return false;
  }
  let tagSelector = [];
  let idSelector = [];
  let classSelector = [];
  let start = selectorStart;
  let currentSelectorName = "";
  for (let c of allSelector) {
    start = start(c)
  }
  start(EOF);
  function selectorStart(c) {
    if (c === '.') {
      return classSelectorName;
    } else if(c === '#') {
      return idSelectorName;
    } else if(c === EOF) {
      return ;
    } else {
      return selectorName(c)
    }
  }
  function classSelectorName(c) {
    if (c === '.' || c === '#' || c === EOF) {
      classSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return classSelectorName;
    }
  }
  function idSelectorName(c) {
    if (c === '.' || c === '#' || c === EOF) {
      idSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return idSelectorName;
    }
  }
  function selectorName(c) {
    if (c === '.' || c === '#' || c === EOF) {
      tagSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return selectorName;
    }
  }
  let count = 0;
  if (idSelector.length !== 0) {
    let attr = element.attributes.filter(attr => attr.name === "id")[0]
    if (attr) {
      let ids = attr.value.split(" ")
      for (let i = 0;i < idSelector.length;i++) {
        if(ids.includes(idSelector[i])) count++;
      }
    }
  }
  if (classSelector.length !== 0) {
    let attr = element.attributes.filter(attr => attr.name === "class")[0]
    if (attr) {
      let classNames = attr.value.split(" ")
      for (let i = 0;i < classSelector.length;i++) {
        if(classNames.includes(classSelector[i])) count++;
      }
    }
  }
  if (tagSelector.length !== 0) {
    if (element.tagName === tagSelector[0]) {
      count ++;
    }
  }
  let sumLength = idSelector.length + classSelector.length + tagSelector.length
  return count === sumLength

  //TODO 新的selector
}
function specificity(selector) {
  let p = [0, 0, 0, 0];
  let tagSelector = [];
  let idSelector = [];
  let classSelector = [];
  let start = selectorStart;
  let currentSelectorName = "";
  for (let c of selector) {
    start = start(c)
  }
  start(EOF);
  p[1] = idSelector.length;
  p[2] = classSelector.length;
  p[3] = tagSelector.length;
  return p

  function selectorStart(c) {
    if (c === '.') {
      return classSelectorName;
    } else if(c === '#') {
      return idSelectorName;
    } else if(c === EOF) {
      return ;
    } else if (c === ' ') {
      return selectorStart;
    } else {
      return selectorName(c)
    }
  }
  function classSelectorName(c) {
    if (c === '.' || c === '#' || c === ' ' || c === EOF) {
      classSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return classSelectorName;
    }
  }
  function idSelectorName(c) {
    if (c === '.' || c === '#' || c === ' ' || c === EOF) {
      idSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return idSelectorName;
    }
  }
  function selectorName(c) {
    if (c === '.' || c === '#' || c === ' ' || c === EOF) {
      tagSelector.push(currentSelectorName);
      currentSelectorName = "";
      return selectorStart(c);
    } else {
      currentSelectorName += c
      return selectorName;
    }
  }
}
function compare(sp1, sp2) {
  if(sp1[0] - sp2[0])
    return sp1[0] - sp2[0]
  if(sp1[1] - sp2[1])
    return sp1[1] - sp2[1]
  if(sp1[2] - sp2[2])
    return sp1[2] - sp2[2]
  return sp1[3] - sp2[3]
}
// todo  复合选择器 div.a#a 都要匹配
function computeCSS(element) {
  let elements = stack.slice().reverse();
  if(!element.computedStyle) 
    element.computedStyle = {}
  for (let rule of rules) {
    let selectorParts = rule.selectors[0].split(" ").reverse();

    if (!match(element, selectorParts[0]))
      continue;

    let matched = false;

    let j = 1;
    for (let i = 0;i<elements.length;i++) {
      if (match(elements[i], selectorParts[j])) {
        j++;
      }
    }
    if (j >= selectorParts.length)
      matched = true;
    if (matched) {
      let sp = specificity(rule.selectors[0]);
      let computedStyle = element.computedStyle;
      for (let declaration of rule.declarations) {
        if (!computedStyle[declaration.property])
          computedStyle[declaration.property] = {}
        if (!computedStyle[declaration.property].specificity) {
          computedStyle[declaration.property].value = declaration.value;
          computedStyle[declaration.property].specificity = sp
        } else if (compare(computedStyle[declaration.property].specificity, sp) < 0)
        computedStyle[declaration.property].value = declaration.value
        computedStyle[declaration.property].specificity = sp
      }
    }
  }
}
function emit(token) {
  let top = stack[stack.length - 1];

  if (token.type === 'startTag') {
    let element = {
      type: 'element',
      children: [],
      attributes: []
    }
    element.tagName = token.tagName;

    for(let p in token) {
      if (p !== 'type' && p !== 'tagName') {
        element.attributes.push({
          name: p,
          value: token[p]
        });
      }
    }
    computeCSS(element)
    top.children.push(element);
    element.parent = top;
    if(!token.isSelfClosing) {
      stack.push(element);
    }
    currentTextNode = null;
  } else if(token.type === 'endTag') {
    if (top.tagName !== token.tagName) {
      throw new Error('Tag start end doesn\'t match !')
    } else {
      // 遇到Style标签时 执行添加css规则的操作
      if (top.tagName === 'style') {
        addCSSRules(top.children[0].content);
      }
      layout(top)
      stack.pop();
    }
    currentTextNode = null;
  } else if (token.type === 'text') {
    if (currentTextNode === null) {
      currentTextNode = {
        type: 'text',
        content: ''
      }
      top.children.push(currentTextNode);
    }
    currentTextNode.content += token.content
  }
}

function data(c) {
  if (c === "<") {
    return tagOpen;
  } else if (c === EOF) {
    emit({
      type: "EOF"
    })
    return ;
  } else {
    emit({
      type: "text",
      content: c
    })
    return data;
  }
}

function tagOpen(c) {
  if (c === "/") {
    return endTagOpen;
  } else if(c.match(/^[a-zA-Z]$/)) {
    currentToken = {
      type: "startTag",
      tagName: ""
    }
    return tagName(c);
  } else {
    return;
  }
}

function endTagOpen(c) {
  if (c.match(/^[a-zA-Z]$/)) {
    currentToken = {
      type: "endTag",
      tagName: ""
    }
    return tagName(c);
  } else if (c === '>') {

