    return UnquotedAttributeValue;
  }
}

function selfClosingStartTag(c) {
  if (c === '>') {
    currentToken.isSelfClosing = true;
    emit(currentToken)
    return data;
  } else if (c === EOF) {
    // throw error
  } else {
